import React, { useState } from "react";
import { Box } from "@mui/material";
import styled from "@emotion/styled";
import CloseFullscreen from '@mui/icons-material/CloseFullscreen';
import "../App.css";


import {Controlled as ControlledEditor } from 'react-codemirror2';

import 'codemirror/lib/codemirror.css';
import 'codemirror/theme/material.css';

// languages,scripts
import 'codemirror/mode/xml/xml';
import 'codemirror/mode/javascript/javascript';
import 'codemirror/mode/css/css';

// styling components using internal tool provided by material ui called as styled()
const Heading = styled(Box)`
  background: #1d1e22;
  display:flex;
  padding: 9px 12px;
`

const Header = styled(Box)`
  display:flex;
  background: #060606;
  color : #AAAEBC;
  justify-content: space-between;
`

const EditorBox = styled(Box)`
  flex-grow : 1;
  flex-basic : 0;
  flex-direction : column;
  padding: 1px 2px 0 2px;
`

const CloseIconWrapper = styled.div`
  cursor: pointer;
`;

export default function Editor({ heading, icon, color, value, onChange}) {

  const [open, setOpen] = useState(true);  

  // handleChange in ControlledEditor
  const handleChange = (editor, data, value) =>{
    onChange(value);
  }

  // const handleIconClick = () => {
  //   if(open == true){
  //     setOpen(false);
  //   }else{
  //     setOpen(true);
  //   }
  //   console.log(open);
  // };

  return (
    // main box
    <EditorBox style={open ? {flexGrow: 1} : {flexGrow: 0} }>

      {/* upper box */}
      <Header>
        <Heading>
          <Box component="span" style={{
            background: color,
            height:20,
            width:20,
            display:"flex",
            placeContent:"center",
            borderRadius:5,
            marginRight:5,
          }}>{icon}</Box>
            {heading}
        </Heading>
        {/* close icon material ui */}
        <CloseFullscreen 
          fontSize="small"  
          onClick={(event) => { console.log('onClick', event)}}
        />
      </Header>

      <ControlledEditor 
        className="controlled-editor"
        value={value}
        onBeforeChange={handleChange}
        options={{ 
          theme:"material",
          lineNumbers:true,
        }}
      />

    </EditorBox>
  );
}
